from datetime import datetime

# Obter os dados enviados pelo n8n (items contém os inputs)
vendas = items  # 'items' contém os dados enviados pelo n8n

# Dicionário para consolidar os dados por vendedor
resultado_por_vendedor = {}

# Processar cada venda
for venda in vendas:
    venda_json = venda.get("json", {})
    vendedor = venda_json.get("vendedor", "").strip()
    data_venda_str = venda_json.get("DataVendas", "")
    valor_venda_raw = venda_json.get("vendas", "0")

    # Se o valor usar vírgula como separador decimal, substitui por ponto
    if isinstance(valor_venda_raw, str):
        valor_venda_raw = valor_venda_raw.replace(',', '.')

    # Verifica se o vendedor existe
    if not vendedor:
        continue

    # Converter o valor para float
    try:
        valor_venda = float(valor_venda_raw)
    except ValueError:
        continue

    # Converter a data para um objeto datetime
    try:
        data_venda = datetime.fromisoformat(data_venda_str.replace("Z", "").replace("+00:00", ""))
    except ValueError:
        continue

    # Inicializa os dados do vendedor, se necessário
    if vendedor not in resultado_por_vendedor:
        resultado_por_vendedor[vendedor] = {
            "totalVendas": 0,         # Soma de todos os valores das vendas
            "quantidadeVendas": 0,    # Número total de vendas
            "ultimaVenda": None,      # Data da venda mais recente
            "valorUltimaVenda": 0     # Valor da venda mais recente
        }

    dados = resultado_por_vendedor[vendedor]
    dados["totalVendas"] += valor_venda
    dados["quantidadeVendas"] += 1

    # Se essa venda for a mais recente, atualiza a data e o valor da última venda
    if not dados["ultimaVenda"] or data_venda > dados["ultimaVenda"]:
        dados["ultimaVenda"] = data_venda
        dados["valorUltimaVenda"] = valor_venda

# Construir a saída final no formato esperado pelo n8n (array de objetos com a propriedade "json")
resultado_final = []
for vendedor, dados in resultado_por_vendedor.items():
    valor_medio = dados["totalVendas"] / dados["quantidadeVendas"] if dados["quantidadeVendas"] > 0 else 0
    resultado_final.append({
        "json": {
            "vendedor": vendedor,
            "totalVendas": round(dados["totalVendas"], 2),
            "quantidadeVendas": dados["quantidadeVendas"],
            "valorMedio": round(valor_medio, 2),
            "ultimaVenda": dados["ultimaVenda"].isoformat() if dados["ultimaVenda"] else None,
            "valorUltimaVenda": round(dados["valorUltimaVenda"], 2)
        }
    })

# Retorna os resultados para o n8n
return resultado_final;

