const axios = require('axios');
const FormData = require('form-data');

const mappingData = $('Mapping').first().json; // Obtém todo o conteúdo de 'Mapping'
console.log("Mapping Data:", mappingData);

const baseWebhookUrl = `http://${mappingData.system.n8n_webhook_service}:5678/webhook/`;
const finalWorkflowPath = mappingData.system.workflow_path;
const defaultFinalUrl = `${baseWebhookUrl}${finalWorkflowPath}`;

const formData = new FormData();

// Unifica os valores de "content" com quebra de linha dupla
const unifiedContent = items
  .map(item => item.json.content)
  .filter(content => content) // Remove itens vazios ou undefined
  .join('\n\n');
console.log("Unified Content:", unifiedContent);

// Monta o JSON separado para o corpo da requisição
const jsonData = {
  content: unifiedContent,
  mapping: mappingData
};

// Adiciona os dados JSON ao FormData
formData.append('content', jsonData.content);
formData.append('mapping', JSON.stringify(jsonData.mapping));

// Adiciona arquivos binários mantendo os nomes originais
items.forEach((item) => {
  if (item.binary) {
    Object.keys(item.binary).forEach((key) => {
      const file = item.binary[key];
      formData.append(key, Buffer.from(file.data, 'base64'), {
        filename: file.fileName || key,
        contentType: file.mimeType,
      });
    });
  }
});

if (mappingData.system.enabled_rag === true || mappingData.system.enabled_rag === 'true') {
  console.log("RAG is enabled. Sending request to the RAG endpoint first.");
  const ragUrl = `${baseWebhookUrl}rag`;
  console.log(`Sending RAG request to: ${ragUrl}`);

  return axios.post(ragUrl, formData, { headers: formData.getHeaders() })
    .then(ragResponse => {
      console.log("RAG Response received:", ragResponse.data);
      // Ajuste: obter o valor de "output" em vez de "rag_return"
      const rag_return = ragResponse.data.output;
      jsonData.rag_return = rag_return; // Inclui o retorno do RAG no JSON

      // Reconstroi o FormData com os dados atualizados (incluindo rag_return)
      const finalFormData = new FormData();
      finalFormData.append('content', jsonData.content);
      finalFormData.append('mapping', JSON.stringify(jsonData.mapping));
      finalFormData.append('rag_return', rag_return);
      items.forEach((item) => {
        if (item.binary) {
          Object.keys(item.binary).forEach((key) => {
            const file = item.binary[key];
            finalFormData.append(key, Buffer.from(file.data, 'base64'), {
              filename: file.fileName || key,
              contentType: file.mimeType,
            });
          });
        }
      });

      // Recupera a URL final do mapping; se não existir, usa a URL padrão
      let finalUrl = mappingData.system.rag_return;
      if (!finalUrl) {
        console.error("mappingData.system.rag_return is undefined. Using default final URL instead.");
        finalUrl = defaultFinalUrl;
      }
      console.log(`Mapping system.rag_return value: "${finalUrl}"`);
      console.log("Sending final request to the RAG final endpoint...");

      return axios.post(finalUrl, finalFormData, { headers: finalFormData.getHeaders() })
        .then(finalResponse => {
          console.log("Final Response received:", finalResponse.data);
          return [{
            json: {
              rag: ragResponse.data,
              final: finalResponse.data,
              logs: [
                `RAG request sent to: ${ragUrl}`,
                `RAG response received with output: ${rag_return}`,
                `Final request sent to: ${finalUrl}`,
                `Final response received successfully.`
              ]
            }
          }];
        })
        .catch(finalError => {
          console.error(`Final request error for URL (${finalUrl}):`, finalError.message);
          return [{
            json: {
              error: finalError.message,
              rag: ragResponse.data,
              logs: [
                `RAG request sent to: ${ragUrl}`,
                `RAG response received with output: ${rag_return}`,
                `Attempted final request to: ${finalUrl}`,
                `Final request error: ${finalError.message}`
              ]
            }
          }];
        });
    })
    .catch(ragError => {
      console.error("RAG request error:", ragError.message);
      return [{
        json: {
          error: ragError.message,
          logs: [
            `RAG request sent to: ${baseWebhookUrl}rag`,
            `RAG request error: ${ragError.message}`
          ]
        }
      }];
    });
} else {
  console.log("RAG is not enabled. Sending request directly to the default final URL:", defaultFinalUrl);
  return axios.post(defaultFinalUrl, formData, { headers: formData.getHeaders() })
    .then(response => {
      console.log("Final Response received:", response.data);
      return [{
        json: {
          final: response.data,
          logs: [
            `Final request sent to: ${defaultFinalUrl}`,
            `Final response received successfully.`
          ]
        }
      }];
    })
    .catch(error => {
      console.error("Final request error:", error.message);
      return [{
        json: {
          error: error.message,
          logs: [
            `Final request error for URL (${defaultFinalUrl}): ${error.message}`
          ]
        }
      }];
    });
}

