  .catch(err => [{ json: { error: err.response?.data || err.message } }]);
}

return [{ json: { message: "Nenhuma ação realizada" } }];

/**
 * Código final para enviar a resposta da AI para o Chatwoot, com três modos:
 * "text", "audio" e "smart".
 *
 * Dependendo do modo, o texto original é segmentado e enviado:
 *  - "text": Divide o texto em blocos de aproximadamente DESIRED_TEXT_SEGMENT_SIZE e envia como texto.
 *  - "audio": Divide o texto em blocos de aproximadamente DESIRED_AUDIO_SEGMENT_SIZE (cerca de 30s de fala),
 *             converte cada segmento para áudio via Elevenlabs e envia como mensagem de áudio.
 *  - "smart": Segmenta o texto em sentenças e, para cada sentença, aplica regras:
 *             • Se contém dígitos ou tem tamanho < 80 ou > 600, envia como texto.
 *             • Caso contrário, agrupa sentenças para áudio (aprox. DESIRED_AUDIO_SEGMENT_SIZE).
 *             Após segmentação, segmentos com menos de MIN_SEGMENT_LENGTH são mesclados.
 *
 * Os envios para o Chatwoot usam o endpoint:
 *   http://{chatwoot_service}:3000/api/v1/accounts/{account_id}/conversations/{conversation_id}/messages
 * O token é enviado no header "api_access_token".
 */

const axios = require('axios');
const FormData = require('form-data');

// Funções de log para console (não serão retornadas)
function logMessage(msg) {
  console.log(msg);
}
function logError(msg) {
  console.error(msg);
}

// ------------------------------
// Configurações Gerais
// ------------------------------
const MIN_TOTAL_LENGTH = 50;               // Texto menor que 50 caracteres: enviar inteiro.
const MIN_SEGMENT_LENGTH = 20;             // Segmentos menores que 20 caracteres serão mesclados.
const DESIRED_TEXT_SEGMENT_SIZE = 200;       // Tamanho ideal para segmentos no modo "text".
const DESIRED_AUDIO_SEGMENT_SIZE = 450;      // Tamanho ideal para segmentos no modo "audio" (~30s).
const DELAY_FACTOR = 200;                  // Delay: 200ms por 100 caracteres.

const delay = ms => new Promise(resolve => setTimeout(resolve, ms));

// ------------------------------
// Parâmetros obtidos dos nós
// ------------------------------
const mapping = $('Mapping').first().json;
const chatwootData = mapping.chatwoot;
const accountId = chatwootData.account_id;
const conversationId = chatwootData.conversation_id;
const botToken = chatwootData.bot_token;

const system = mapping.system;
const outputFormat = system.output_format.toLowerCase(); // "text", "audio" ou "smart"
const elevenlabsKey = system.elevenlabs_key;
const elevenlabsVoice = system.elevenlabs_voice || "JBFqnCBsd6RMkjVDRZzb";
const elevenlabsModel = system.elevenlabs_model || "eleven_multilingual_v2";
const chatwootService = system.chatwoot_service; // Valor para a URL do Chatwoot

// Texto original (ajustado para acessar o payload final corretamente)
const originalText = $('Execute Core').first().json.final.output;
logMessage(`[Main] Texto original possui ${originalText.length} caracteres.`);
logMessage(`[Main] Modo de saída: ${outputFormat}`);

// Endpoint do Chatwoot (ajustado para usar chatwootService)
const chatwootUrl = `http://${chatwootService}:3000/api/v1/accounts/${encodeURIComponent(accountId)}/conversations/${encodeURIComponent(conversationId)}/messages`;
logMessage(`[Main] Chatwoot URL: ${chatwootUrl}`);

// ------------------------------
// Função para enviar mensagem de texto
// ------------------------------
async function sendTextMessage(content) {
  const payload = { content };
  const headers = {
    "api_access_token": botToken,
    "Content-Type": "application/json"
  };
  logMessage(`[sendTextMessage] Enviando mensagem de texto.`);
  logMessage(`[sendTextMessage] URL: ${chatwootUrl}`);
  logMessage(`[sendTextMessage] Payload: ${JSON.stringify(payload)}`);
  logMessage(`[sendTextMessage] Headers: ${JSON.stringify(headers)}`);
  try {
    const response = await axios.post(chatwootUrl, payload, { headers });
    logMessage(`[sendTextMessage] Resposta do Chatwoot: status ${response.status}, data: ${JSON.stringify(response.data)}`);
    return response.data;
  } catch (error) {
    logError(`[sendTextMessage] Erro ao enviar mensagem de texto: ${error.toString()}`);
    logError(`[sendTextMessage] Erro Response: ${error.response ? JSON.stringify(error.response.data) : 'N/A'}`);
    throw error;
  }
}

// ------------------------------
// Função para enviar mensagem de áudio
// ------------------------------
async function sendAudioMessage(audioBuffer) {
  const form = new FormData();
  form.append("attachments[]", audioBuffer, { filename: "audio.mp3", contentType: "audio/mpeg" });
  const headers = {
    "api_access_token": botToken,
    ...form.getHeaders()
  };
  logMessage(`[sendAudioMessage] Enviando mensagem de áudio.`);
  logMessage(`[sendAudioMessage] URL: ${chatwootUrl}`);
  logMessage(`[sendAudioMessage] Headers: ${JSON.stringify(headers)}`);
  try {
    const response = await axios.post(chatwootUrl, form, { headers });
    logMessage(`[sendAudioMessage] Resposta do Chatwoot: status ${response.status}, data: ${JSON.stringify(response.data)}`);
    return response.data;
  } catch (error) {
    logError(`[sendAudioMessage] Erro ao enviar mensagem de áudio: ${error.toString()}`);
    logError(`[sendAudioMessage] Erro Response: ${error.response ? JSON.stringify(error.response.data) : 'N/A'}`);
    throw error;
  }
}

// ------------------------------
// Função para converter texto em áudio via Elevenlabs
// ------------------------------
async function convertTextToSpeech(text) {
  const url = `https://api.elevenlabs.io/v1/text-to-speech/${encodeURIComponent(elevenlabsVoice)}?output_format=mp3_44100_128`;
  const headers = {
    "xi-api-key": elevenlabsKey,
    "Content-Type": "application/json"
  };
  const payload = {
    text: text,
    model_id: elevenlabsModel
  };
  logMessage(`[convertTextToSpeech] Convertendo texto para áudio (primeiros 50 caracteres): "${text.substring(0,50)}..."`);
  try {
    const response = await axios.post(url, payload, { headers, responseType: 'arraybuffer' });
    logMessage(`[convertTextToSpeech] Conversão realizada com sucesso. Status: ${response.status}`);
    return response.data;
  } catch (error) {
    logError(`[convertTextToSpeech] Erro ao converter texto para áudio: ${error.toString()}`);
    logError(`[convertTextToSpeech] Erro Response: ${error.response ? JSON.stringify(error.response.data) : 'N/A'}`);
    throw error;
  }
}

// ------------------------------
// Função para mesclar segmentos curtos (para modos text e audio)
// ------------------------------
function mergeShortSegments(segments, minLength) {
  const merged = [];
  let buffer = "";
  for (const seg of segments) {
    if ((buffer + " " + seg).trim().length < minLength) {
      buffer = (buffer + " " + seg).trim();
    } else {
      if (buffer) {
        merged.push(buffer);
        buffer = "";
      }
      merged.push(seg);
    }
  }
  if (buffer) {
    if (merged.length > 0) {
      merged[merged.length - 1] += " " + buffer;
    } else {
      merged.push(buffer);
    }
  }
  return merged;
}

// ------------------------------
// Função de segmentação genérica (evita quebrar palavras)
// ------------------------------
function segmentTextGeneric(text, desiredMaxLength) {
  if (text.length <= desiredMaxLength) return [text];

  let segments = [];
  // Tenta dividir por quebra de linha dupla
  if (text.includes("\n\n")) {
    segments = text.split(/\n\s*\n/).map(seg => seg.trim()).filter(seg => seg.length > 0);
  }
  // Se não houver, ou se não gerar resultados, dividir por delimitadores naturais (ponto, ?, !, vírgula, ;, :)
  if (segments.length === 0) {
    segments = text.split(/(?<=[.?!,;:])\s+/).map(seg => seg.trim()).filter(seg => seg.length > 0);
  }

  // Mescla segmentos curtos
  segments = mergeShortSegments(segments, MIN_SEGMENT_LENGTH);

  // Se algum segmento for muito longo, subdivide-o por espaços, sem quebrar palavras
  const finalSegments = [];
  segments.forEach(seg => {
    if (seg.length > desiredMaxLength * 1.5) {
      const words = seg.split(" ");
      let current = "";
      for (const word of words) {
        if ((current + " " + word).trim().length > desiredMaxLength) {
          finalSegments.push(current.trim());
          current = word;
        } else {
          current += " " + word;
        }
      }
      if (current.trim()) finalSegments.push(current.trim());
    } else {
      finalSegments.push(seg);
    }
  });

  return finalSegments;
}

// ------------------------------
// Funções de segmentação para cada modo
// ------------------------------
function segmentForText(text) {
  return segmentTextGeneric(text, DESIRED_TEXT_SEGMENT_SIZE);
}

function segmentForAudio(text) {
  return segmentTextGeneric(text, DESIRED_AUDIO_SEGMENT_SIZE);
}

/**
 * Função "smartSegment":
 * - Divide o texto em sentenças usando delimitadores finais.
 * - Para cada sentença, se ela contiver dígitos ou tiver menos de 80 ou mais de 600 caracteres,
 *   ela será marcada para envio como texto.
 * - Caso contrário, a sentença é considerada "audio".
 * - As sentenças marcadas para áudio são agrupadas até atingirem aproximadamente DESIRED_AUDIO_SEGMENT_SIZE.
 * - Após a segmentação, os segmentos do tipo "text" com menos que MIN_SEGMENT_LENGTH são mesclados.
 * 
 * Retorna um array de objetos: { type: "text" | "audio", content: string }.
 */
function smartSegment(text) {
  const sentences = text.split(/(?<=[.?!])\s+/).map(s => s.trim()).filter(s => s);
  const segments = [];
  let audioBuffer = "";

  for (const sentence of sentences) {
    if (/\d/.test(sentence) || sentence.length < 80 || sentence.length > 600) {
      if (audioBuffer) {
        segments.push({ type: "audio", content: audioBuffer.trim() });
        audioBuffer = "";
      }
      segments.push({ type: "text", content: sentence });
    } else {
      if ((audioBuffer + " " + sentence).trim().length > DESIRED_AUDIO_SEGMENT_SIZE) {
        segments.push({ type: "audio", content: audioBuffer.trim() });
        audioBuffer = sentence;
      } else {
        audioBuffer = (audioBuffer + " " + sentence).trim();
      }
    }
  }
  if (audioBuffer) {
    segments.push({ type: "audio", content: audioBuffer.trim() });
  }

  const mergedSegments = [];
  for (let i = 0; i < segments.length; i++) {
    const seg = segments[i];
    if (seg.type === "text" && seg.content.length < MIN_SEGMENT_LENGTH) {
      if (mergedSegments.length > 0 && mergedSegments[mergedSegments.length - 1].type === "text") {
        mergedSegments[mergedSegments.length - 1].content += " " + seg.content;
      } else if (i < segments.length - 1 && segments[i+1].type === "text") {
        segments[i+1].content = seg.content + " " + segments[i+1].content;
      } else {
        mergedSegments.push(seg);
      }
    } else {
      mergedSegments.push(seg);
    }
  }

  return mergedSegments;
}

// ------------------------------
// Função principal para segmentar e enviar a mensagem
// ------------------------------
async function main() {
  try {
    logMessage(`[Main] Modo de saída selecionado: ${outputFormat}`);

    if (outputFormat === "audio") {
      const segments = segmentForAudio(originalText);
      logMessage(`[Main] Texto segmentado em ${segments.length} segmento(s) para áudio.`);
      for (const segment of segments) {
        const audioBuffer = await convertTextToSpeech(segment);
        await sendAudioMessage(audioBuffer);
        logMessage(`[Main] Segmento de áudio enviado: "${segment.substring(0,50)}..."`);
        const delayTime = Math.ceil(segment.length / 100) * DELAY_FACTOR;
        logMessage(`[Main] Aguardando ${delayTime}ms antes do próximo segmento de áudio.`);
        await delay(delayTime);
      }
      return { message: "Mensagem de áudio enviada com sucesso." };
    } else if (outputFormat === "smart") {
      const segments = smartSegment(originalText);
      logMessage(`[Main] Texto segmentado em ${segments.length} parte(s) (smart mode).`);
      for (const seg of segments) {
        if (seg.type === "audio") {
          const audioBuffer = await convertTextToSpeech(seg.content);
          await sendAudioMessage(audioBuffer);
          logMessage(`[Main] Segmento de áudio enviado: "${seg.content.substring(0,50)}..."`);
        } else {
          await sendTextMessage(seg.content);
          logMessage(`[Main] Segmento de texto enviado: "${seg.content.substring(0,50)}..."`);
        }
        const delayTime = Math.ceil(seg.content.length / 100) * DELAY_FACTOR;
        logMessage(`[Main] Aguardando ${delayTime}ms antes do próximo segmento.`);
        await delay(delayTime);
      }
      return { message: "Mensagem smart enviada com sucesso." };
    } else {
      const segments = segmentForText(originalText);
      logMessage(`[Main] Texto segmentado em ${segments.length} segmento(s) para texto.`);
      for (const segment of segments) {
        await sendTextMessage(segment);
        logMessage(`[Main] Segmento de texto enviado: "${segment.substring(0,50)}..."`);
        const delayTime = Math.ceil(segment.length / 100) * DELAY_FACTOR;
        logMessage(`[Main] Aguardando ${delayTime}ms antes do próximo segmento de texto.`);
        await delay(delayTime);
      }
      return { message: "Mensagens de texto enviadas com sucesso." };
    }
  } catch (error) {
    logError(`[Main] Erro: ${error.toString()}`);
    return { error: error.toString() };
  }
}

return main().then(result => [{ json: result }]);

const axios = require('axios');

// Delay configurável
function delay(ms) {
  return new Promise(resolve => setTimeout(resolve, ms));
}

// Executa requisição GET com retry, usando parâmetros configuráveis.
async function axiosGetWithRetry(url, config, maxAttempts = 3, delayMs = 1000) {
  let attempt = 1;
  while (attempt <= maxAttempts) {
    try {
      return await axios.get(url, config);
    } catch (err) {
      console.error(`Tentativa ${attempt} para ${url} falhou:`, {
        message: err.message,
        status: err.response?.status,
        data: err.response?.data,
      });
      if (attempt === maxAttempts) throw err;
      attempt++;
      await delay(delayMs);
    }
  }
}

// Prompt para gerar resumo de um bloco de texto
