// Cria chunks de forma orgânica, sem misturar conteúdos de diferentes scrapings
function createChunks(text, chunkLength, overlapPercentage) {
  const overlapLength = Math.floor(chunkLength * (overlapPercentage / 100));
  const chunks = [];
  let currentPos = 0;
  while (currentPos < text.length) {
    let endPos = currentPos + chunkLength;
    if (endPos >= text.length) {
      endPos = text.length;
    } else {
      const searchStart = Math.max(currentPos, endPos - 100);
      let breakPos = -1;
      let pos = text.lastIndexOf("\n\n", endPos);
      if (pos >= searchStart) {
        breakPos = pos + 2;
      } else {
        pos = text.lastIndexOf("\n", endPos);
        if (pos >= searchStart) {
          breakPos = pos + 1;
        } else {
          pos = text.lastIndexOf(".", endPos);
          if (pos >= searchStart) {
            breakPos = pos + 1;
          } else {
            pos = text.lastIndexOf("?", endPos);
            if (pos >= searchStart) {
              breakPos = pos + 1;
            } else {
              pos = text.lastIndexOf("!", endPos);
              if (pos >= searchStart) {
                breakPos = pos + 1;
              }
            }
          }
        }
      }
      if (breakPos > currentPos) {
        endPos = breakPos;
      }
    }
    const chunk = text.substring(currentPos, endPos).trim();
    if (chunk.length > 0) chunks.push(chunk);
    if (endPos === text.length) break;
    currentPos = endPos - overlapLength;
    if (currentPos < 0) currentPos = 0;
  }
  return chunks;
}

// Função para atualizar o campo "description" da tabela "knowledge" no Directus
async function updateDirectusDescription(newDescription, globalVars, globalId) {
  const patchUrl = `http://${globalVars.directus_service}:8055/items/knowledge/${globalId}`;
  const patchPayload = { description: newDescription };
  const headers = {
    "Content-Type": "application/json",
    "Authorization": `Bearer ${globalVars.directus_token}`
  };
  let attempts = 0;
  while (attempts < 3) {
    try {
      const response = await axios.patch(patchUrl, patchPayload, { headers });
      console.log(`Directus update successful: ${JSON.stringify(response.data)}`);
      return response.data;
    } catch (error) {
      attempts++;
      console.error(`Directus update attempt ${attempts} failed: ${error.message}`);
      if (attempts < 3) {
        await delay(1000);
      } else {
        throw error;
      }
    }
  }
}

async function processScraping() {
  // Extrai dados do nó Get Global Vars
  const globalData = $('Get Global Vars').first().json;
  const globalVars = globalData.global_vars;
  const filesIds = Array.isArray(globalData.files_id) ? globalData.files_id : [];
  const originalUrls = Array.isArray(globalData.urls) ? globalData.urls : [];
  // Usa s3_url definido em global_vars
  const host = globalVars.s3_url;
  const directusToken = globalVars.directus_token;
  const jinaToken = globalVars.jina_ai_token;
  const openrouterKey = globalVars.openrouter_key;
  const directusService = globalVars.directus_service;
  const n8nWebhookService = globalVars.n8n_webhook_service;
  const chunkLength = globalData.chunks_length || 2000; // fallback default
  const overlapPercentage = globalData.overlap || 0;      // fallback default

  // Extrai o valor de Knowledge Trigger
  const knowledgeTriggerId = $('Knowledge Trigger').first().json.payload.id;

  // Processa arquivos para obter finalUrl com retry (se existirem)
  const fileResults = await Promise.all(
    filesIds.map(async (fileId) => {
      try {
        const response = await axiosGetWithRetry(
          `http://${directusService}:8055/files/${fileId}`,
          { headers: { Authorization: `Bearer ${directusToken}` } }
        );
        const filename_disk = response.data.data.filename_disk;
        // Retira o "s3" pois já está incluso na URL
        const finalUrl = `${host}/directus/${filename_disk}`;
        return { type: 'file', fileId, url: finalUrl };
      } catch (err) {
        return {
          type: 'file',
          fileId,
          error: err.message,
          status: err.response?.status,
          responseData: err.response?.data,
        };
      }
    })
  );

  // Prepara URLs para scraping (se existirem)
  const originalUrlsToScrape = originalUrls.map(url => ({ type: 'original', url }));
  const allUrlsToScrape = [
    ...fileResults.filter(item => item.url),
    ...originalUrlsToScrape
  ];

  // Executa scraping com retry para cada URL (se houver)
  const scrapeResults = await Promise.all(
    allUrlsToScrape.map(async (item) => {
      try {
        const scrapingUrl = `https://r.jina.ai/${item.url}`;
        const response = await axiosGetWithRetry(
          scrapingUrl,
          { headers: { Authorization: `Bearer ${jinaToken}` } }
        );
        return { ...item, scrapeData: response.data };
      } catch (err) {
        return {
          ...item,
          scrapeError: err.message,
          status: err.response?.status,
          errorData: err.response?.data,
        };
      }
    })
  );

  // Agrega e limpa conteúdo dos scrapings para gerar chunks e descrição
  const scrapedTexts = scrapeResults.map(item => item.scrapeData ? JSON.stringify(item.scrapeData) : '');
  const aggregatedContent = scrapedTexts.join(' ');
  const cleanedContent = aggregatedContent.replace(/[^a-zA-Z0-9\s]/g, ' ');

  // Gera chunks dos scrapings (mantendo conteúdo orgânico por scraping)
  const chunks = [];
  scrapeResults.forEach(item => {
    if (item.scrapeData) {
      const text = JSON.stringify(item.scrapeData);
      const itemChunks = createChunks(text, chunkLength, overlapPercentage);
      itemChunks.forEach(chunk => {
        chunks.push({ chunk, source: item.url });
      });
    }
  });

  // Geração da descrição do conteúdo
  const blockSize = 200000;
  let finalDescription;
  async function enviarPrompt(promptContent) {
    const response = await axios.post(
      'https://openrouter.ai/api/v1/chat/completions',
      {
        model: 'google/gemini-pro',
        messages: [{ role: 'user', content: promptContent }]
      },
      { headers: { Authorization: `Bearer ${openrouterKey}` } }
    );
    return response.data;
  }
  if (cleanedContent.length > blockSize) {
    const blocks = [];
    for (let i = 0; i < cleanedContent.length; i += blockSize) {
      blocks.push(cleanedContent.substring(i, i + blockSize));
    }
    const summaries = await Promise.all(
      blocks.map(async (block) => {
        const prompt = `<dados>${block}</dados>\n` + SUMMARY_PROMPT.replace('{conteudo}', block);
        try {
          const summaryResponse = await enviarPrompt(prompt);
          return (summaryResponse.choices && summaryResponse.choices[0].message.content) || '';
        } catch (err) {
          return '';
        }
      })
    );
    const aggregatedSummaries = summaries.join(' ');
    const descriptionPrompt = `<dados>${aggregatedSummaries}</dados>\n` + FINAL_DESCRIPTION_PROMPT.replace('{conteudo}', aggregatedSummaries);
    try {
      const descriptionResponse = await enviarPrompt(descriptionPrompt);
      finalDescription = descriptionResponse;
    } catch (err) {
      finalDescription = { error: err.message, status: err.response?.status, errorData: err.response?.data };
    }
  } else {
    const directPrompt = `<dados>${cleanedContent}</dados>\n` + DIRECT_DESCRIPTION_PROMPT.replace('{conteudo}', cleanedContent);
    try {
      const descriptionResponse = await enviarPrompt(directPrompt);
      finalDescription = descriptionResponse;
    } catch (err) {
      finalDescription = { error: err.message, status: err.response?.status, errorData: err.response?.data };
    }
  }

  // Extrai somente o conteúdo da descrição, se disponível
  if (finalDescription && finalDescription.choices && finalDescription.choices[0] && finalDescription.choices[0].message) {
    finalDescription = finalDescription.choices[0].message.content;
  }

  // Atualiza o campo "description" na tabela "knowledge" no Directus
  try {
    const updateResponse = await updateDirectusDescription(finalDescription, globalVars, globalData.id);
    console.log("Directus update response:", updateResponse);
  } catch (error) {
    console.error("Falha ao atualizar o Directus após 3 tentativas:", error.message);
  }

  // Geração dos vetores a partir dos chunks
  for (const chunkItem of chunks) {
    const vectorPromptBody = {
      messages: [
        {
          role: "user",
          content: `Você é um agente interno de um sistema especializado em extrair perguntas e respostas de documentos. Sua tarefa é analisar o chunk fornecido, que representa um segmento de um documento completo, e gerar perguntas e respostas baseadas nas informações contidas nele. 

Contexto do documento: ${globalData.Instruction_Prompt}

Diretrizes:
1. Extraia informações relevantes (respostas) do chunk fornecido. Essas informações devem ser associadas a perguntas principais, mantendo sempre o tom e o estilo do texto original.
2. Para cada resposta extraída:
   - Crie uma pergunta principal (q) que tenha como resposta a informação extraída (a).
   - Identifique o trecho específico do documento (t) de onde a resposta foi retirada.
   - Formule perguntas alternativas (sq) que poderiam ser feitas para obter a mesma resposta (a).
3. Sempre que encontrar múltiplas informações relevantes, crie múltiplas perguntas e respostas.
4. Mantenha a originalidade e fidelidade ao conteúdo fornecido.

Chunk:
${chunkItem.chunk}`
        }
      ],
      response_format: {
        type: "json_schema",
        json_schema: {
          name: "extracted_data",
          strict: true,
          schema: {
            type: "object",
            properties: {
              results: {
                type: "array",
                items: {
                  type: "object",
                  properties: {
                    a: { type: "string", description: "A resposta ou informação extraída." },
                    t: { type: "string", description: "O trecho exato do documento de onde a resposta foi retirada." },
                    q: { type: "string", description: "A pergunta principal que leva à resposta." },
                    sq: { type: "array", items: { type: "string" }, description: "Perguntas alternativas que poderiam obter a mesma resposta." }
                  },
                  required: ["a", "t", "q", "sq"],
                  additionalProperties: false
                }
              }
            },
            required: ["results"],
            additionalProperties: false
          }
        }
      }
    };
    try {
      const vectorResponse = await axios.post(
        'https://openrouter.ai/api/v1/chat/completions',
        vectorPromptBody,
        { headers: { Authorization: `Bearer ${openrouterKey}` } }
      );
      // Envia a resposta para o webhook incluindo global_vars, knowledge_trigger_id, pinecone_host e namespace
      await axios.post(
        `http://${n8nWebhookService}:5678/webhook/vector`,
        {
          ...vectorResponse.data,
          global_vars: globalVars,
          knowledge_trigger_id: knowledgeTriggerId,
          pinecone_host: globalData.pinecone_host,
          namespace: globalData.namespace
        }
      );
    } catch (err) {
      console.error('Erro ao gerar vetor para chunk:', err.message);
    }
    // Aguarda 10 segundos antes da próxima requisição
    await delay(10000);
  }

  return { files: fileResults, scraping: scrapeResults, description: finalDescription, chunks };
}

return processScraping()
