          console.log("Valores convertidos:", values);
        }
      } else {
        values = [values];
        logs.push("Valores encapsulados em array: " + trimLog(JSON.stringify(values)));
        console.log("Valores encapsulados:", values);
      }

      const upsertBody = {
        vectors: [
          {
            id: payload.data.id,
            values: values,
            metadata: {
              a: payload.data.answer,
              t: payload.data.context,
              q: payload.data.question,
              sq: similarQuestionsArray
            }
          }
        ],
        namespace: payload.data.namespace
      };
      logs.push("Body para upsert (Get Dimensions): " + trimLog(JSON.stringify(upsertBody)));
      console.log("Body de upsert:", upsertBody);

      options = {
        method: "POST",
        uri: endpoint,
        headers: pineconeHeaders,
        body: upsertBody,
        json: true
      };

      response = await this.helpers.request(options);
      logs.push("Resposta do upsert recebida.");
      console.log("Resposta do upsert:", trimLog(JSON.stringify(response)));

      mainResponse = response;
    } else {
      logs.push("Operação não compatível. Nenhuma requisição enviada.");
      console.log("Operação não compatível.");
      mainResponse = { message: "Operação não compatível." };
    }

    // Atualiza a tabela vectors_status para os casos ENABLED, DISABLED, INSERT ou UPDATE (ou DELETE)
    if (["ENABLED", "DISABLED", "INSERT", "UPDATE", "DELETE"].includes(op)) {
      const statusValue = payload.data.enabled === true ? "#2ECDA7" : "#E35169";
      const directusStatusHost = "http://" + payload.global_vars.directus_service + ":8055";
      const directusStatusEndpoint = directusStatusHost + "/items/vectors_status";
      const directusHeaders = {
        "Authorization": "Bearer " + payload.global_vars.directus_token,
        "Content-Type": "application/json"
      };
      logs.push("Atualizando tabela vectors_status em: " + directusStatusEndpoint);
      console.log("Directus vectors_status endpoint:", directusStatusEndpoint);

      const statusQueryOptions = {
        method: "GET",
        uri: directusStatusEndpoint,
        qs: { "filter[vector_id][_eq]": payload.data.id },
        headers: directusHeaders,
        json: true
      };

      let statusRecord = await this.helpers.request(statusQueryOptions);
      logs.push("Resposta da consulta vectors_status: " + trimLog(JSON.stringify(statusRecord)));
      console.log("Directus vectors_status GET response:", trimLog(JSON.stringify(statusRecord)));

      const currentTimestamp = new Date().toISOString();
      const statusBody = {
        value: statusValue,
        vector_id: payload.data.id,
        date_created: currentTimestamp,
        date_updated: currentTimestamp
      };

      if (statusRecord && statusRecord.data && statusRecord.data.length > 0) {
        const recordId = statusRecord.data[0].id;
        const statusUpdateEndpoint = directusStatusEndpoint + "/" + recordId;
        logs.push("Atualizando registro vectors_status com id: " + recordId);
        console.log("Atualizando vectors_status em:", statusUpdateEndpoint);

        const statusUpdateOptions = {
          method: "PATCH",
          uri: statusUpdateEndpoint,
          headers: directusHeaders,
          body: statusBody,
          json: true
        };

        var statusResponse = await this.helpers.request(statusUpdateOptions);
        logs.push("Resposta atualização vectors_status: " + trimLog(JSON.stringify(statusResponse)));
        console.log("Directus vectors_status update response:", trimLog(JSON.stringify(statusResponse)));
      } else {
        logs.push("Nenhum registro vectors_status encontrado, criando novo.");
        console.log("Criando novo registro vectors_status.");

        const statusCreateOptions = {
          method: "POST",
          uri: directusStatusEndpoint,
          headers: directusHeaders,
          body: statusBody,
          json: true
        };

        var statusResponse = await this.helpers.request(statusCreateOptions);
        logs.push("Resposta criação vectors_status: " + trimLog(JSON.stringify(statusResponse)));
        console.log("Directus vectors_status create response:", trimLog(JSON.stringify(statusResponse)));
      }
    }

    return [{
      json: {
        mainResponse,
        logs
      }
    }];
  } catch (error) {
    console.error("Erro:", error);
    logs.push("Erro: " + error.message);
    return [{
      json: {
        error: error.message,
        logs
      }
    }];
  }
}

return main();

const axios = require('axios');

async function runTool() {
  let logs = "";

  try {
    // Obter parâmetros de entrada
    logs += "Obtendo parâmetros de entrada...\n";
    const searchQuery = query.query;
    const knowledgeId = query.knowledge_id;
    logs += `searchQuery: ${searchQuery}\nknowledgeId: ${knowledgeId}\n`;

    // Obter mapping a partir do nó "RAG Input"
    let mappingInput = $('RAG Input').first().json.body.mapping;
    if (typeof mappingInput === 'string') {
      try {
        mappingInput = JSON.parse(mappingInput);
        logs += "Mapping Input convertido de string para objeto.\n";
      } catch (e) {
        throw new Error("Falha ao converter mapping para objeto.");
      }
    }
    logs += "Mapping Input obtido.\n";

    if (!mappingInput.system) {
      throw new Error("mappingInput.system está indefinido.");
    }

    // Gerar embeddings usando a API do OpenAI
    logs += "Chamando endpoint de embeddings da OpenAI...\n";
    const openaiKey = mappingInput.system.openai_key;
    const embeddingResponse = await axios.post(
      'https://api.openai.com/v1/embeddings',
      {
        input: searchQuery,
        model: "text-embedding-3-small"
      },
      {
        headers: {
          "Authorization": `Bearer ${openaiKey}`,
          "Content-Type": "application/json"
        }
      }
    );
    logs += "Embeddings obtidos com sucesso.\n";
    const embedding = embeddingResponse.data.data[0].embedding;
    logs += `Embedding (primeiros 5 valores): ${embedding.slice(0,5).join(", ")}...\n`;

    // Obter pinecone_host e namespace do histórico
    logs += "Obtendo pinecone_host e namespace a partir do histórico...\n";
    const historyKnowledge = $('Get History').first().json.knowledge;
    const knowledgeItem = historyKnowledge.find(item => item.id === knowledgeId);
    if (!knowledgeItem) {
      throw new Error(`Conhecimento com id ${knowledgeId} não encontrado.`);
    }
    const pineconeHostRaw = knowledgeItem.pinecone_host;
    const namespace = knowledgeItem.namespace;
    logs += `pineconeHostRaw: ${pineconeHostRaw}, namespace: ${namespace}\n`;

    // Garantir que pinecone_host comece com "https://"
    let pineconeHost = pineconeHostRaw;
    if (!pineconeHost.startsWith("https://")) {
      pineconeHost = "https://" + pineconeHost;
    }
    logs += `pineconeHost final: ${pineconeHost}\n`;

    // Requisição para a API do Pinecone
    logs += "Chamando API do Pinecone...\n";
    const pineconeKey = mappingInput.system.pinecone_key;
    const pineconeQueryUrl = `${pineconeHost}/query`;
    const pineconeResponse = await axios.post(
      pineconeQueryUrl,
      {
        namespace: namespace,
        vector: embedding,
        topK: 3,
        includeValues: false,
        includeMetadata: true
      },
      {
        headers: {
          "Api-Key": pineconeKey,
          "X-Pinecone-API-Version": "2024-10",
          "Content-Type": "application/json"
        }
      }
    );
    logs += "Resposta do Pinecone obtida com sucesso.\n";
    logs += `Pinecone response data: ${JSON.stringify(pineconeResponse.data)}\n`;

    // Processar os resultados do Pinecone usando as chaves corretas dos metadados ("a", "q" e "t")
    const matches = pineconeResponse.data.matches;
    matches.forEach((m, idx) => {
      logs += `Match ${idx + 1}: id=${m.id}, score=${m.score}, metadata=${JSON.stringify(m.metadata)}\n`;
    });

    const filteredMatches = matches.filter(item => item.score >= 0.0)
      .map(item => ({
        vector: item.id,
        score: item.score,
        answer: item.metadata ? item.metadata.a : null,
        question: item.metadata ? item.metadata.q : null,
        context: item.metadata ? item.metadata.t : null
      }));
    logs += `Matches filtrados: ${JSON.stringify(filteredMatches)}\n`;

    const resultString = `Os vetores retornados foram: ${JSON.stringify(filteredMatches)}`;
    logs += "Processamento final concluído.\n";

    console.log("Logs:", logs);
    // Retorna apenas o resultado final (string) sem os logs.
    return resultString;
  } catch (error) {
    logs += `Erro: ${error.message}\n`;
    console.error("Erro:", error.message, "Logs:", logs);
    return `Erro: ${error.message}`;
  }
}

return runTool();

