const mapping = JSON.parse($('Core Input').item.json.body.mapping);

// Verifica se o usu√°rio √© admin; caso contr√°rio, retorna erro
if (mapping.system.user_type !== 'admin') {
  return JSON.stringify({ success: false, error: "Acesso negado: usu√°rio n√£o autorizado." });
}

/**
 * Fun√ß√£o que espera pelo tempo especificado.
 * @param {number} ms - Milissegundos a aguardar.
 * @returns {Promise<void>}
 */
const delay = ms => new Promise(resolve => setTimeout(resolve, ms));

/**
 * Fun√ß√£o para enviar a requisi√ß√£o para criar uma conversa no Directus.
 * Tenta at√© 3 vezes em caso de falha, com delay de 1 segundo entre as tentativas.
 * @returns {Promise<object>} - Objeto com sucesso ou erro.
 */
async function sendRequest() {
  const url = `http://${mapping.system.directus_service}:8055/items/conversations`;
  const data = {
    user_id: mapping.chatwoot.contact_id,
    date_created: new Date().toISOString()
  };
  const headers = {
    Authorization: `Bearer ${mapping.system.directus_token}`,
    'Content-Type': 'application/json'
  };

  let attempt = 0;
  let lastError = null;

  while (attempt < 3) {
    try {
      const response = await axios.post(url, data, { headers });
      console.log(`Requisi√ß√£o realizada com sucesso na tentativa ${attempt + 1}.`);
      return { success: true, data: response.data };
    } catch (error) {
      attempt++;
      lastError = error;
      console.error(`Tentativa ${attempt} falhou:`, error.response ? error.response.data : error.message);
      if (attempt < 3) {
        console.log("Aguardando 1 segundo antes da nova tentativa...");
        await delay(1000);
      }
    }
  }

  return { success: false, error: lastError.response ? lastError.response.data : lastError.message };
}

return sendRequest().then(result => JSON.stringify(result));

